import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import './home.css'


function TypingText({ text, speed, onComplete }) {
  const [displayedText, setDisplayedText] = useState("");
  const [index, setIndex] = useState(0);
  const [isBlinking, setIsBlinking] = useState(true);

  // Step 1: Blink cursor 3 times before typing starts
  useEffect(() => {
    const blinkTimeout = setTimeout(() => {
      setIsBlinking(false); // Stop blinking after 3 seconds
    }, 3000);

    return () => clearTimeout(blinkTimeout);
  }, []);

  // Step 2: Start typing effect after blinking
  useEffect(() => {
    if (!isBlinking && index < text.length) {
      const timeout = setTimeout(() => {
        setDisplayedText((prev) => prev + text[index]);
        setIndex(index + 1);
      }, speed);

      return () => clearTimeout(timeout);
    } else if (index === text.length && onComplete) {
      onComplete(); // Trigger next text to start
    }
  }, [index, text, speed, isBlinking, onComplete]);

  return (
    <span className="typing-text">
      {isBlinking ? <span className="cursor">|</span> : displayedText}
      {!isBlinking && <span className="cursor">|</span>}
    </span>
  );
}

function Home() {
  const [showSecondText, setShowSecondText] = useState(false);

  return (
    <div className="home">
      <div className="hero">
        <div className="text-section">
          <h1>
            <TypingText text="Hi, my name is Robb." speed={100} onComplete={() => setShowSecondText(true)} />
          </h1>
          {showSecondText && (
            <p>
              <TypingText text="I'm an independent creative developer from Abergavenny, South Wales." speed={50} />
            </p>
          )}
        </div>
        <div className="image-section">
          <img src="/avatar.png" alt="Avatar" className="avatar" />
        </div>
      </div>
      <div className="scroll-text">SCROLL</div>
    </div>
  );
}

export default Home;
