import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import './home.css'


function TypingText({ text, speed, onComplete }) {
  const [displayedText, setDisplayedText] = useState("");
  const [index, setIndex] = useState(0);
  const [isBlinking, setIsBlinking] = useState(true);
  const [showCursor, setShowCursor] = useState(true);

  useEffect(() => {
    // Step 1: Blink cursor 3 times before typing starts
    const blinkTimeout = setTimeout(() => {
      setIsBlinking(false); // Stop blinking after 3 seconds
    }, 3000);

    return () => clearTimeout(blinkTimeout);
  }, []);

  useEffect(() => {
    // Step 2: Start typing effect after blinking
    if (!isBlinking && index < text.length) {
      const timeout = setTimeout(() => {
        setDisplayedText((prev) => prev + text[index]);
        setIndex(index + 1);
      }, speed);

      return () => clearTimeout(timeout);
    } else if (index === text.length) {
      setTimeout(() => setShowCursor(false), 500); // Hide cursor when done
      if (onComplete) onComplete();
    }
  }, [index, text, speed, isBlinking, onComplete]);

  return (
    <span className="typing-text">
      {isBlinking ? <span className="cursor">|</span> : displayedText}
      {showCursor && !isBlinking && <span className="cursor">|</span>}
    </span>
  );
}

function Home() {
  return (
    <div className="home">
      <div className="hero">
        <div className="text-section">
          <TypingText
            text={`Hi, my \nname is Robb.\n\nI'm an independent creative developer from \nAbergavenny, South Wales.`}
            speed={50}
            boldText="Hi, my \nname is Robb."
            normalText="\n\nI'm an independent creative developer from \nAbergavenny, South Wales."
          />
        </div>

        <div className="image-section">
          <img src="/avatar.png" alt="Avatar" className="avatar" />
        </div>
      </div>
      <div className="scroll-text">SCROLL</div>
    </div>
  );
}
export default Home;
